mapper.select:
SELECT * FROM todo
WHERE user_id = #{userId}
ORDER BY priority DESC, due_date ASC, created_at DESC

mapper.update:
UPDATE todo
title = #{title}, description = #{description}, completed = #{completed}, category = #{category}, priority = #{priority}, due_date = #{dueDate},
WHERE id = #{id}

